import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import {
  Box,
  Typography,
  Button,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Grid,
} from '@mui/material';
import { BarChart as ChartIcon, Add as AddIcon } from '@mui/icons-material';
import PageHeader from '../components/common/PageHeader';

function Visualizations() {
  const location = useLocation();
  const [openDialog, setOpenDialog] = useState(false);

  useEffect(() => {
    if (location.state?.openCreateDialog) {
      setOpenDialog(true);
      // Clear the state to prevent reopening on refresh
      window.history.replaceState({}, document.title);
    }
  }, [location]);

  const handleCreateClick = () => {
    setOpenDialog(true);
  };

  const handleCloseDialog = () => {
    setOpenDialog(false);
  };

  return (
    <Box sx={{ p: 3 }}>
      <PageHeader
        title="Visualizations"
        icon={<ChartIcon />}
        actions={
          <Button
            variant="contained"
            startIcon={<AddIcon />}
            onClick={handleCreateClick}
          >
            Create Visualization
          </Button>
        }
      />

      <Grid container spacing={3} sx={{ mt: 2 }}>
        <Grid item xs={12}>
          <Typography variant="body1" color="text.secondary">
            No visualizations created yet. Click the button above to create your first visualization.
          </Typography>
        </Grid>
      </Grid>

      {/* Create Visualization Dialog */}
      <Dialog
        open={openDialog}
        onClose={handleCloseDialog}
        maxWidth="md"
        fullWidth
      >
        <DialogTitle>Create New Visualization</DialogTitle>
        <DialogContent>
          <Typography>
            Select a data source and visualization type to get started.
          </Typography>
          {/* Add visualization creation form here */}
        </DialogContent>
        <DialogActions>
          <Button onClick={handleCloseDialog}>Cancel</Button>
          <Button variant="contained" onClick={handleCloseDialog}>
            Create
          </Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
}

export default Visualizations;
