import React, { useState, useCallback } from 'react';
import { useDropzone } from 'react-dropzone';
import {
  Box,
  Paper,
  Typography,
  Button,
  LinearProgress,
  Alert,
  IconButton,
} from '@mui/material';
import CloudUploadIcon from '@mui/icons-material/CloudUpload';
import CloseIcon from '@mui/icons-material/Close';
import DescriptionIcon from '@mui/icons-material/Description';
import InsertDriveFileIcon from '@mui/icons-material/InsertDriveFile';

function FileUploader({ onUpload, allowedTypes = ['text/csv', 'application/json'] }) {
  const [files, setFiles] = useState([]);
  const [uploading, setUploading] = useState(false);
  const [uploadProgress, setUploadProgress] = useState(0);
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(false);

  const onDrop = useCallback((acceptedFiles) => {
    setError(null);
    
    // Check file types
    const invalidFiles = acceptedFiles.filter(file => !allowedTypes.includes(file.type));
    if (invalidFiles.length > 0) {
      setError(`Invalid file type. Please upload only ${allowedTypes.map(type => type.split('/')[1].toUpperCase()).join(' or ')} files.`);
      return;
    }
    
    setFiles(acceptedFiles);
  }, [allowedTypes]);

  const { getRootProps, getInputProps, isDragActive } = useDropzone({
    onDrop,
    accept: {
      'text/csv': ['.csv'],
      'application/json': ['.json'],
    },
    maxFiles: 1,
  });

  const handleUpload = async () => {
    if (files.length === 0) return;

    setUploading(true);
    setUploadProgress(0);
    setError(null);
    setSuccess(false);

    const file = files[0];
    const formData = new FormData();
    formData.append('file', file);

    try {
      // Simulate progress for demo purposes
      const interval = setInterval(() => {
        setUploadProgress((prevProgress) => {
          const newProgress = prevProgress + 10;
          if (newProgress >= 100) {
            clearInterval(interval);
            return 100;
          }
          return newProgress;
        });
      }, 300);

      // Call the onUpload callback with formData
      const result = await onUpload(formData);

      clearInterval(interval);
      setUploadProgress(100);
      setSuccess(true);
      setTimeout(() => {
        setFiles([]);
        setUploading(false);
        setUploadProgress(0);
      }, 1500);
    } catch (err) {
      setError(err.message || 'An error occurred while uploading the file');
      setUploading(false);
      setUploadProgress(0);
    }
  };

  const removeFile = () => {
    setFiles([]);
    setError(null);
    setSuccess(false);
  };

  return (
    <Box sx={{ width: '100%' }}>
      {error && (
        <Alert 
          severity="error" 
          sx={{ mb: 2 }}
          action={
            <IconButton
              aria-label="close"
              color="inherit"
              size="small"
              onClick={() => setError(null)}
            >
              <CloseIcon fontSize="inherit" />
            </IconButton>
          }
        >
          {error}
        </Alert>
      )}
      
      {success && (
        <Alert 
          severity="success" 
          sx={{ mb: 2 }}
          action={
            <IconButton
              aria-label="close"
              color="inherit"
              size="small"
              onClick={() => setSuccess(false)}
            >
              <CloseIcon fontSize="inherit" />
            </IconButton>
          }
        >
          File uploaded successfully!
        </Alert>
      )}
      
      <Paper
        sx={{
          p: 4,
          borderRadius: 2,
          backgroundColor: 'background.paper',
          border: isDragActive ? '2px dashed #2196f3' : '2px dashed #e0e0e0',
          cursor: 'pointer',
          transition: 'border 0.3s ease-in-out',
        }}
        {...getRootProps()}
      >
        <input {...getInputProps()} />
        
        {files.length === 0 ? (
          <Box sx={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
            <CloudUploadIcon sx={{ fontSize: 60, mb: 2, color: 'primary.main' }} />
            <Typography variant="h6" gutterBottom>
              {isDragActive ? 'Drop your file here...' : 'Drag & drop your file here'}
            </Typography>
            <Typography variant="body2" color="text.secondary" align="center" sx={{ mb: 2 }}>
              or click to browse your files
            </Typography>
            <Typography variant="caption" color="text.secondary">
              Supported formats: {allowedTypes.map(type => type.split('/')[1].toUpperCase()).join(', ')}
            </Typography>
          </Box>
        ) : (
          <Box sx={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between' }}>
            <Box sx={{ display: 'flex', alignItems: 'center' }}>
              {files[0].type === 'text/csv' ? (
                <DescriptionIcon sx={{ fontSize: 40, mr: 2, color: 'success.main' }} />
              ) : (
                <InsertDriveFileIcon sx={{ fontSize: 40, mr: 2, color: 'info.main' }} />
              )}
              <Box>
                <Typography variant="subtitle1">{files[0].name}</Typography>
                <Typography variant="caption" color="text.secondary">
                  {Math.round(files[0].size / 1024)} KB | {files[0].type}
                </Typography>
              </Box>
            </Box>
            <IconButton onClick={(e) => { e.stopPropagation(); removeFile(); }} disabled={uploading}>
              <CloseIcon />
            </IconButton>
          </Box>
        )}
      </Paper>

      {files.length > 0 && (
        <Box sx={{ mt: 2 }}>
          {uploading && (
            <Box sx={{ mb: 2 }}>
              <LinearProgress variant="determinate" value={uploadProgress} />
              <Typography variant="caption" color="text.secondary" sx={{ mt: 0.5, display: 'block' }}>
                Uploading... {uploadProgress}%
              </Typography>
            </Box>
          )}
          <Button
            variant="contained"
            color="primary"
            onClick={handleUpload}
            disabled={uploading}
            fullWidth
            startIcon={<CloudUploadIcon />}
          >
            Upload
          </Button>
        </Box>
      )}
    </Box>
  );
}

export default FileUploader;
