import { 
  Container, 
  Typography, 
  Grid, 
  Card, 
  CardContent, 
  CardMedia,
  Box,
  Divider,
  Stack,
  Chip,
  useTheme,
  Avatar
} from '@mui/material';
import { styled } from '@mui/material/styles';
import { useColorMode } from '../App';
import { useEffect } from 'react';

const StyledSection = styled(Box)(({ theme }) => ({
  marginBottom: theme.spacing(6),
}));

const teamMembers = [
  {
    name: 'John Doe',
    role: 'Frontend Developer',
    contributions: 'UI design, React components, dashboard',
    color: '#1976d2' // Primary color for avatar
  },
  {
    name: 'Jane Smith',
    role: 'Backend Developer',
    contributions: 'APIs, file upload, JWT auth',
    color: '#009688' // Teal color for avatar
  },
  {
    name: 'Alex Roy',
    role: 'AI Engineer',
    contributions: 'Chart recommendation engine',
    color: '#673ab7' // Deep Purple for avatar
  },
  {
    name: 'Priya Patel',
    role: 'Full-stack',
    contributions: 'MongoDB schema, integration, bug fixing',
    color: '#ff5722' // Deep Orange for avatar
  }
];

const techStack = [
  'React.js',
  'Material UI',
  'Node.js',
  'Express',
  'MongoDB',
  'Mongoose',
  'Chart.js'
];

export default function AboutUs() {
  const theme = useTheme();
  const { mode } = useColorMode();
  
  useEffect(() => {
    // Load LinkedIn badge script
    const script = document.createElement('script');
    script.src = "https://platform.linkedin.com/badges/js/profile.js";
    script.async = true;
    script.defer = true;
    script.type = "text/javascript";
    document.body.appendChild(script);
    
    return () => {
      // Cleanup script when component unmounts
      document.body.removeChild(script);
    };
  }, []);
  
  return (
    <Container maxWidth="lg" sx={{ py: 4 }}>
      {/* Mission Section */}
      <StyledSection>
        <Typography variant="h2" gutterBottom>
          About DataViz Pro
        </Typography>
        <Typography variant="h5" color="text.secondary" paragraph>
          Transforming data visualization through AI-powered insights
        </Typography>
        <Typography variant="body1" paragraph>
          DataViz Pro revolutionizes how you interact with data by automatically recommending
          the most effective visualizations for your datasets. Our platform combines
          cutting-edge AI with intuitive design to make data visualization accessible to everyone.
        </Typography>
      </StyledSection>

      {/* Features Section */}
      <StyledSection>
        <Typography variant="h4" gutterBottom>
          Key Features
        </Typography>
        <Grid container spacing={3}>
          <Grid item xs={12} md={4}>
            <Card elevation={2} sx={{ height: '100%', transition: 'transform 0.3s', '&:hover': { transform: 'translateY(-5px)' } }}>
              <CardContent>
                <Typography variant="h6" gutterBottom>Smart Upload</Typography>
                <Typography>
                  Seamless CSV/JSON file upload with automatic data parsing
                </Typography>
              </CardContent>
            </Card>
          </Grid>
          <Grid item xs={12} md={4}>
            <Card elevation={2} sx={{ height: '100%', transition: 'transform 0.3s', '&:hover': { transform: 'translateY(-5px)' } }}>
              <CardContent>
                <Typography variant="h6" gutterBottom>AI Recommendations</Typography>
                <Typography>
                  Intelligent chart suggestions based on your data structure
                </Typography>
              </CardContent>
            </Card>
          </Grid>
          <Grid item xs={12} md={4}>
            <Card elevation={2} sx={{ height: '100%', transition: 'transform 0.3s', '&:hover': { transform: 'translateY(-5px)' } }}>
              <CardContent>
                <Typography variant="h6" gutterBottom>Interactive Charts</Typography>
                <Typography>
                  Dynamic, responsive visualizations powered by Chart.js
                </Typography>
              </CardContent>
            </Card>
          </Grid>
        </Grid>
      </StyledSection>

      {/* Tech Stack Section */}
      <StyledSection>
        <Typography variant="h4" gutterBottom>
          Tech Stack
        </Typography>
        <Stack direction="row" spacing={1} flexWrap="wrap" gap={1} sx={{ marginTop: 2 }}>
          {techStack.map((tech) => (
            <Chip 
              key={tech} 
              label={tech} 
              color="primary" 
              sx={{ 
                fontSize: '0.875rem',
                fontWeight: 500
              }} 
            />
          ))}
        </Stack>
      </StyledSection>

      {/* Team Section with LinkedIn Badge */}
      <StyledSection>
        <Typography variant="h4" gutterBottom>
          Meet Our Team
        </Typography>
        <Grid container spacing={4} justifyContent="center">
          <Grid item xs={12} md={6} lg={4}>
            <Card elevation={3} sx={{ overflow: 'hidden' }}>
              <CardContent>
                <Box sx={{ 
                  display: 'flex', 
                  justifyContent: 'center',
                  flexDirection: 'column',
                  '& .LI-profile-badge': { width: '100%' },
                  '& iframe': { width: '100%' }
                }}>
                  {mode === 'light' ? (
                    <div className="badge-base LI-profile-badge" data-locale="en_US" data-size="medium" data-theme="light" data-type="HORIZONTAL" data-vanity="aaditya-desai1" data-version="v1"><a className="badge-base__link LI-simple-link" href="https://in.linkedin.com/in/aaditya-desai1?trk=profile-badge">Aaditya Desai</a></div>
                  ) : (
                    <div className="badge-base LI-profile-badge" data-locale="en_US" data-size="medium" data-theme="dark" data-type="HORIZONTAL" data-vanity="aaditya-desai1" data-version="v1"><a className="badge-base__link LI-simple-link" href="https://in.linkedin.com/in/aaditya-desai1?trk=profile-badge">Aaditya Desai</a></div>
                  )}
                </Box>
              </CardContent>
            </Card>
          </Grid>
        </Grid>
      </StyledSection>

      {/* Future Scope Section */}
      <StyledSection>
        <Typography variant="h4" gutterBottom>
          Future Scope
        </Typography>
        <Typography variant="body1" paragraph>
          We're constantly working to enhance DataViz Pro. Our roadmap includes:
        </Typography>
        <Box sx={{ pl: 3 }}>
          <Typography component="li" sx={{ mb: 1 }}>Excel and spreadsheet support</Typography>
          <Typography component="li" sx={{ mb: 1 }}>Real-time collaboration features</Typography>
          <Typography component="li" sx={{ mb: 1 }}>Cloud storage integration</Typography>
          <Typography component="li" sx={{ mb: 1 }}>Advanced AI-powered analytics</Typography>
        </Box>
      </StyledSection>
    </Container>
  );
}
