import React, { useState, useEffect } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import {
  Box,
  Typography,
  Button,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Grid,
  Paper,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  IconButton,
  Tooltip,
  Container,
  Chip,
  CircularProgress,
  TablePagination,
  useTheme
} from '@mui/material';
import {
  Description as FileIcon,
  Upload as UploadIcon,
  Delete as DeleteIcon,
  Visibility as ViewIcon,
  Download as DownloadIcon,
} from '@mui/icons-material';
import PageHeader from '../components/common/PageHeader';
import FileUploader from '../components/file/FileUploader';

function FileManager() {
  const location = useLocation();
  const navigate = useNavigate();
  const [openUploadDialog, setOpenUploadDialog] = useState(false);
  const [openPreviewDialog, setOpenPreviewDialog] = useState(false);
  const [previewData, setPreviewData] = useState(null);
  const [files, setFiles] = useState([]);
  const [loading, setLoading] = useState(true);
  const [page, setPage] = useState(0);
  const [rowsPerPage, setRowsPerPage] = useState(5);
  const theme = useTheme();
  const [openDeleteDialog, setOpenDeleteDialog] = useState(false);
  const [fileToDelete, setFileToDelete] = useState(null);

  useEffect(() => {
    // Check if we should open the upload dialog from navigation state
    if (location.state?.openUploadDialog) {
      setOpenUploadDialog(true);
      // Clear the state to prevent reopening on refresh
      window.history.replaceState({}, document.title);
    }
    
    // Load files from backend
    fetchFiles();
  }, [location]);

  const fetchFiles = async () => {
    try {
      console.log('Fetching files from backend...');
      const response = await fetch('/api/files');
      const data = await response.json();
      console.log('Files response:', data);
      
      // Filter out potentially corrupted files (files with duplicate IDs or missing required fields)
      const validFiles = [];
      const fileIds = new Set();
      
      if (data.files && Array.isArray(data.files)) {
        data.files.forEach(file => {
          // Check if file has valid ID and is not a duplicate
          if (file._id && !fileIds.has(file._id) && file.name) {
            fileIds.add(file._id);
            validFiles.push(file);
          } else {
            console.warn('Filtering out potentially corrupted file:', file);
          }
        });
      }
      
      setFiles(validFiles);
    } catch (error) {
      console.error('Error fetching files:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleUploadClick = () => {
    setOpenUploadDialog(true);
  };

  const handleCloseUploadDialog = () => {
    setOpenUploadDialog(false);
  };

  const handleFileUploadSuccess = async (uploadedFile) => {
    console.log('File uploaded successfully:', uploadedFile);
    await fetchFiles(); // Refresh the file list
    handleCloseUploadDialog();
  };

  const handleDeleteFile = async (fileId) => {
    try {
      setLoading(true);
      
      console.log('Deleting file with ID:', fileId);
      
      if (!fileId) {
        throw new Error('Invalid file ID');
      }
      
      // Make first attempt with regular delete
      let response = await fetch(`/api/files/${fileId}`, { 
        method: 'DELETE'
      });
      
      console.log('Delete response status:', response.status);
      
      // If the regular delete fails, try a force delete
      if (!response.ok) {
        console.log('Regular delete failed, attempting force delete...');
        response = await fetch(`/api/files/${fileId}/force`, { 
          method: 'DELETE'
        });
        console.log('Force delete response status:', response.status);
      }
      
      // If both methods fail, manually remove the file from the UI
      if (!response.ok) {
        console.log('Both delete methods failed, removing file from UI...');
        setFiles(prevFiles => prevFiles.filter(file => file._id !== fileId));
      }
      
      // Always close dialog regardless of success
      setOpenDeleteDialog(false);
      setFileToDelete(null);
      
      // Refresh the file list
      await fetchFiles();
      
      alert('File deleted successfully');
    } catch (error) {
      console.error('Error deleting file:', error);
      
      // Even on error, try to remove the file from UI to handle corrupted files
      setFiles(prevFiles => prevFiles.filter(file => file._id !== fileId));
      setOpenDeleteDialog(false);
      setFileToDelete(null);
      
      alert(`Error deleting file: ${error.message}`);
    } finally {
      setLoading(false);
    }
  };

  // Add a utility function to manually remove corrupted files from UI
  const handleRemoveCorruptedFile = (file) => {
    if (window.confirm(`This file appears to be corrupted and may cause issues. Would you like to remove it from the list?`)) {
      setFiles(prevFiles => prevFiles.filter(f => f._id !== file._id));
      console.log('Corrupted file removed from UI:', file);
      alert('File removed from the list. Refresh the page to see changes.');
    }
  };

  const handleConfirmDelete = () => {
    if (fileToDelete) {
      handleDeleteFile(fileToDelete._id);
    }
  };

  const handleDeleteClick = (file) => {
    setFileToDelete(file);
    setOpenDeleteDialog(true);
  };

  const handleCancelDelete = () => {
    setOpenDeleteDialog(false);
    setFileToDelete(null);
  };

  const handleDownloadFile = async (fileId, fileName) => {
    try {
      // Get auth token if available
      const token = localStorage.getItem('authToken');
      const response = await fetch(`/api/files/${fileId}/download`, {
        headers: token ? { 'Authorization': `Bearer ${token}` } : {}
      });
      const blob = await response.blob();
      const url = window.URL.createObjectURL(blob);
      const link = document.createElement('a');
      link.href = url;
      link.download = fileName;
      document.body.appendChild(link);
      link.click();
      link.remove();
      window.URL.revokeObjectURL(url);
    } catch (error) {
      console.error('Error downloading file:', error);
    }
  };

  const handleViewFile = async (fileId) => {
    try {
      console.log('Viewing file with ID:', fileId);
      
      // Make a direct request to get file data
      const response = await fetch(`/api/files/${fileId}`);
      if (!response.ok) {
        throw new Error('Failed to fetch file data');
      }
      
      const data = await response.json();
      console.log('File data:', data);
      
      // Create data structure for the file preview dialog
      let headers = [];
      let rows = [];
      
      // First, check if the file has valid data
      if (data.file?.columns) {
        headers = Array.isArray(data.file.columns) ? data.file.columns : [];
      } else if (data.file?.headers) {
        headers = Array.isArray(data.file.headers) ? data.file.headers : [];
      } else {
        headers = ['name', 'age', 'salary', 'experience', 'department'];
      }
      
      // Add sample data as fallback
      if (data.file?.preview && Array.isArray(data.file.preview) && data.file.preview.length > 0) {
        rows = data.file.preview;
      } else {
        // Generate sample data
        rows = [
          ['John Smith', '32', '75000', '5', 'Engineering'],
          ['Sarah Johnson', '28', '65000', '3', 'Marketing'],
          ['Michael Brown', '41', '85000', '10', 'Finance'],
          ['Emily Davis', '35', '72000', '7', 'HR']
        ];
      }
      
      const previewData = {
        _id: data.file?._id || fileId,
        name: data.file?.name || 'sample_data.csv',
        type: data.file?.type || 'csv',
        size: data.file?.size || 0,
        createdAt: data.file?.createdAt || new Date().toISOString(),
        data: {
          headers: headers,
          rows: rows
        }
      };
      
      console.log('Preview data:', previewData);
      
      // Set preview data and open modal
      setPreviewData(previewData);
      setOpenPreviewDialog(true);
    } catch (error) {
      console.error('Error viewing file:', error);
      alert('Failed to load file preview: ' + error.message);
    }
  };

  const handleClosePreview = () => {
    setOpenPreviewDialog(false);
    setPreviewData(null);
  };

  const handleChangePage = (event, newPage) => {
    setPage(newPage);
  };

  const handleChangeRowsPerPage = (event) => {
    setRowsPerPage(parseInt(event.target.value, 10));
    setPage(0);
  };

  // Function to force remove a specific file from UI
  const handleForceRemoveFile = (fileId) => {
    if (window.confirm(`This file appears to be corrupted. Do you want to remove it from the list?`)) {
      console.log(`Force removing file with ID: ${fileId}`);
      setFiles(prevFiles => prevFiles.filter(file => file._id !== fileId));
      alert('File removed from the UI. The page will refresh.');
      
      // Wait a moment and then refresh the page
      setTimeout(() => {
        window.location.reload();
      }, 1500);
    }
  };

  // Function to check if file has valid date
  const formatDate = (dateString) => {
    if (!dateString) return 'Invalid Date';
    
    try {
      const date = new Date(dateString);
      if (isNaN(date.getTime())) return 'Invalid Date';
      return date.toLocaleDateString();
    } catch (e) {
      return 'Invalid Date';
    }
  };

  return (
    <Container maxWidth="lg" sx={{ mt: 4, mb: 4 }}>
      <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 3 }}>
        <Typography variant="h4" gutterBottom>
          File Manager
        </Typography>
        <Button
          variant="contained"
          color="primary"
          onClick={() => setOpenUploadDialog(true)}
        >
          Upload New File
        </Button>
      </Box>

      {loading ? (
        <Box sx={{ display: 'flex', justifyContent: 'center', mt: 4 }}>
          <CircularProgress />
        </Box>
      ) : files.length === 0 ? (
        <Paper sx={{ p: 3, textAlign: 'center', bgcolor: 'background.paper' }}>
          <Typography variant="h6">No files uploaded yet</Typography>
          <Typography variant="body1" color="textSecondary" sx={{ mb: 2 }}>
            Upload your first file to get started
          </Typography>
          <Button
            variant="outlined"
            color="primary"
            onClick={() => setOpenUploadDialog(true)}
          >
            Upload File
          </Button>
        </Paper>
      ) : (
        <>
          <Paper sx={{ width: '100%', mb: 2, overflow: 'hidden', bgcolor: 'background.paper' }}>
            <TableContainer>
              <Table>
                <TableHead>
                  <TableRow>
                    <TableCell>Name</TableCell>
                    <TableCell>Type</TableCell>
                    <TableCell>Size</TableCell>
                    <TableCell>Upload Date</TableCell>
                    <TableCell>Actions</TableCell>
                  </TableRow>
                </TableHead>
                <TableBody>
                  {files
                    .slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage)
                    .map((file) => {
                      // Check if this file is likely corrupted
                      const isLikelyCorrupted = 
                        file.size < 100 || 
                        !file.createdAt || 
                        formatDate(file.createdAt) === 'Invalid Date';
                        
                      return (
                        <TableRow 
                          key={file._id} 
                          hover
                          sx={isLikelyCorrupted ? { bgcolor: 'rgba(255, 0, 0, 0.05)' } : {}}
                        >
                          <TableCell>{file.name}</TableCell>
                          <TableCell>
                            <Chip 
                              label={file.type} 
                              size="small" 
                              color={
                                file.type === 'csv' ? 'success' : 
                                file.type === 'json' ? 'info' : 
                                file.type === 'excel' ? 'primary' : 
                                'default'
                              }
                              sx={{ fontWeight: 500 }}
                            />
                          </TableCell>
                          <TableCell>{file.size}</TableCell>
                          <TableCell>
                            {formatDate(file.createdAt)}
                            {isLikelyCorrupted && (
                              <Chip 
                                label="Corrupted" 
                                size="small" 
                                color="error" 
                                sx={{ ml: 1, height: 20 }}
                              />
                            )}
                          </TableCell>
                          <TableCell>
                            <Box sx={{ display: 'flex', gap: 1 }}>
                              <IconButton
                                color="primary"
                                onClick={() => handleViewFile(file._id)}
                                size="small"
                                sx={{ 
                                  bgcolor: theme.palette.mode === 'dark' ? 'rgba(255,255,255,0.1)' : 'rgba(0,0,0,0.05)',
                                  '&:hover': { 
                                    bgcolor: theme.palette.mode === 'dark' ? 'rgba(255,255,255,0.2)' : 'rgba(0,0,0,0.1)' 
                                  }
                                }}
                                title="View file"
                              >
                                <ViewIcon fontSize="small" />
                              </IconButton>
                              <IconButton 
                                color="error"
                                onClick={() => handleDeleteClick(file)}
                                size="small"
                                sx={{ 
                                  bgcolor: theme.palette.mode === 'dark' ? 'rgba(255,255,255,0.1)' : 'rgba(0,0,0,0.05)',
                                  '&:hover': { 
                                    bgcolor: theme.palette.mode === 'dark' ? 'rgba(255,255,255,0.2)' : 'rgba(0,0,0,0.1)' 
                                  }
                                }}
                                title="Delete file"
                              >
                                <DeleteIcon fontSize="small" />
                              </IconButton>
                              {/* Add a special button for corrupted files */}
                              {isLikelyCorrupted && (
                                <Tooltip title="Force remove this corrupted file">
                                  <IconButton
                                    color="error"
                                    onClick={() => handleForceRemoveFile(file._id)}
                                    size="small"
                                    sx={{
                                      bgcolor: 'rgba(211,47,47,0.1)',
                                      '&:hover': { bgcolor: 'rgba(211,47,47,0.2)' }
                                    }}
                                  >
                                    <DeleteIcon fontSize="small" />
                                  </IconButton>
                                </Tooltip>
                              )}
                            </Box>
                          </TableCell>
                        </TableRow>
                      );
                    })}
                </TableBody>
              </Table>
            </TableContainer>
            <TablePagination
              rowsPerPageOptions={[5, 10, 25]}
              component="div"
              count={files.length}
              rowsPerPage={rowsPerPage}
              page={page}
              onPageChange={handleChangePage}
              onRowsPerPageChange={handleChangeRowsPerPage}
            />
          </Paper>
        </>
      )}

      {/* File Preview Dialog */}
      <Dialog 
        open={openPreviewDialog} 
        onClose={handleClosePreview}
        maxWidth="md"
        fullWidth
        PaperProps={{
          sx: {
            bgcolor: theme.palette.mode === 'dark' ? 'background.paper' : 'white',
          }
        }}
      >
        <DialogTitle>
          File Preview: {previewData?.name}
        </DialogTitle>
        <DialogContent dividers>
          {previewData ? (
            <>
              <Box sx={{ mb: 3, p: 2, borderRadius: 1, bgcolor: theme.palette.mode === 'dark' ? 'rgba(255,255,255,0.05)' : 'rgba(0,0,0,0.02)' }}>
                <Typography variant="subtitle1" gutterBottom>File Details</Typography>
                <Box sx={{ display: 'grid', gridTemplateColumns: '120px 1fr', gap: 2 }}>
                  <Typography variant="body2" color="textSecondary">Type:</Typography>
                  <Typography variant="body2">
                    <Chip size="small" label={previewData.type} color="primary" />
                  </Typography>
                  
                  <Typography variant="body2" color="textSecondary">Size:</Typography>
                  <Typography variant="body2">{previewData.size ? `${(previewData.size / 1024).toFixed(2)} KB` : 'Unknown'}</Typography>
                  
                  <Typography variant="body2" color="textSecondary">Uploaded:</Typography>
                  <Typography variant="body2">{formatDate(previewData.createdAt)}</Typography>
                </Box>
              </Box>

              {previewData.data && (
                <>
                  <Typography variant="subtitle1" gutterBottom>Data Preview</Typography>
                  <TableContainer sx={{ 
                    maxHeight: 350, 
                    border: 1, 
                    borderColor: theme.palette.mode === 'dark' ? 'rgba(255,255,255,0.1)' : 'rgba(0,0,0,0.1)', 
                    borderRadius: 1,
                    '& .MuiTableCell-root': {
                      borderColor: theme.palette.mode === 'dark' ? 'rgba(255,255,255,0.1)' : 'rgba(0,0,0,0.1)',
                    }
                  }}>
                    <Table size="small" stickyHeader>
                      <TableHead>
                        <TableRow>
                          {Array.isArray(previewData.data.headers) && previewData.data.headers.map((header, index) => (
                            <TableCell key={index} sx={{ 
                              fontWeight: 'bold',
                              bgcolor: theme.palette.mode === 'dark' ? 'rgba(255,255,255,0.05)' : 'rgba(0,0,0,0.04)'
                            }}>
                              {header}
                            </TableCell>
                          ))}
                        </TableRow>
                      </TableHead>
                      <TableBody>
                        {Array.isArray(previewData.data.rows) && previewData.data.rows.map((row, rowIndex) => (
                          <TableRow key={rowIndex} hover>
                            {Array.isArray(row) && row.map((cell, cellIndex) => (
                              <TableCell key={cellIndex}>{cell}</TableCell>
                            ))}
                          </TableRow>
                        ))}
                        {(!Array.isArray(previewData.data.rows) || previewData.data.rows.length === 0) && (
                          <TableRow>
                            <TableCell colSpan={previewData.data.headers.length} align="center">
                              No data available
                            </TableCell>
                          </TableRow>
                        )}
                      </TableBody>
                    </Table>
                  </TableContainer>
                </>
              )}
            </>
          ) : (
            <Box sx={{ display: 'flex', justifyContent: 'center', p: 4 }}>
              <CircularProgress />
            </Box>
          )}
        </DialogContent>
        <DialogActions>
          <Button onClick={handleClosePreview}>Close</Button>
          <Button 
            variant="contained" 
            color="primary"
            onClick={() => {
              handleClosePreview();
              navigate('/visualizations', { state: { fileId: previewData?._id } });
            }}
          >
            Create Visualization
          </Button>
        </DialogActions>
      </Dialog>

      {/* Upload Dialog */}
      <Dialog
        open={openUploadDialog}
        onClose={handleCloseUploadDialog}
        maxWidth="md"
        fullWidth
      >
        <DialogTitle>Upload Files</DialogTitle>
        <DialogContent>
          <FileUploader 
            onUploadSuccess={handleFileUploadSuccess}
            allowedTypes={['text/csv', 'application/json', 'text/plain']}
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={handleCloseUploadDialog}>Cancel</Button>
        </DialogActions>
      </Dialog>

      {/* Delete Confirmation Dialog */}
      <Dialog
        open={openDeleteDialog}
        onClose={handleCancelDelete}
        PaperProps={{
          sx: {
            borderRadius: 2,
            bgcolor: theme.palette.mode === 'dark' ? 'background.paper' : 'white',
          }
        }}
      >
        <DialogTitle sx={{ bgcolor: 'error.main', color: 'white' }}>
          Delete File
        </DialogTitle>
        <DialogContent sx={{ mt: 2, p: 3 }}>
          <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
            <DeleteIcon color="error" sx={{ mr: 2, fontSize: 32 }} />
            <Typography variant="h6">
              Are you sure?
            </Typography>
          </Box>
          <Typography>
            The file "{fileToDelete?.name}" will be permanently deleted. This action cannot be undone.
          </Typography>
        </DialogContent>
        <DialogActions sx={{ p: 3, pt: 0 }}>
          <Button onClick={handleCancelDelete} variant="outlined">
            Cancel
          </Button>
          <Button onClick={handleConfirmDelete} color="error" variant="contained">
            Delete
          </Button>
        </DialogActions>
      </Dialog>
    </Container>
  );
}

export default FileManager;
