        body: JSON.stringify({
          name: chartData.name,
          description: chartData.description || 'AI generated visualization',
          fileId: chartData.fileId,
          chartType: chartData.chartType,
          config: chartData.config,
          confidence: chartData.confidence || 85,
          isAIGenerated: true
